mapper.select:
SELECT BRD_SQ , MBR_SQ , BRD_TTL , BRD_CONT , BRD_DEL_YN , BRD_RECMMND_CNT , BRD_VW_CNT , BRD_SLT_YN , BRD_SLT_CMMT_SQ , BRD_DIV_CD , BRD_DTL_DIV_CD , BRD_NOTI_DIV_CD , BRD_SLT_DIV_CD , BRD_REGIST_DTM , BRD_MDFY_DTM FROM est.tbl_board_m WHERE BRD_DIV_CD = #{category}
AND BRD_DTL_DIV_CD = #{detailCategory}
AND BRD_DEL_YN = 'N' AND BRD_NOTI_DIV_CD = 'N' ORDER BY BRD_REGIST_DTM DESC
